// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
interface ERC20Interface { function totalSupply() external view returns (uint256);

function balanceOf(address tokenOwner)
    external
    view
    returns (uint256 balance);

function transfer(address to, uint256 tokens)
    external
    
    returns (bool success);

function allowance(address tokenOwner, address spender)
    external
    view
    returns (uint256 remaining);

function approve(address spender, uint256 tokens)
    external
    returns (bool success);

function transferFrom(
    address from,
    address to,
    uint256 tokens
) external returns (bool success);

event Transfer(address indexed from, address indexed to, uint256 tokens);
event Approval(
    address indexed tokenOwner,
    address indexed spender,
    uint256 tokens
);
}

contract ERC20Token is ERC20Interface { string public name; string public symbol; uint8 public decimals; uint256 public _totalSupply; mapping(address => uint256) public balances; mapping(address => mapping(address => uint256)) public allowed;

constructor(
    string memory _name,
    string memory _symbol,
    uint8 _decimals,
    uint256 _initialSupply
) {
    name = _name;
    symbol = _symbol;
    decimals = _decimals;
    _totalSupply = _initialSupply;
    balances[msg.sender] = _totalSupply;
}

function transfer(address to, uint256 value)
    public
    override
    returns (bool)
{
    require(balances[msg.sender] >= value, "not sufficient balance");
    balances[msg.sender] -= value;
    balances[to] += value;
    emit Transfer(msg.sender, to, value);
    return true;
}

function transferFrom(
    address from,
    address to,
    uint256 value
) public override returns (bool) {
    require(allowed[from][msg.sender] >= value, "allowance too low");
    require(balances[from] >= value, "token balance too low");
    allowed[from][msg.sender] -= value; //allowed[accounts[0]][accounts[1]]-100
    balances[from] -= value;
    balances[to] += value;
    emit Transfer(from, to, value);
    return true;
}

function approve(address spender, uint256 value)
    public
    override
    returns (bool)
{
    allowed[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
}

function allowance(address owner, address spender)
    public
    view
    override
    returns (uint256)
{
    return allowed[owner][spender];
}

function balanceOf(address owner) public view override returns (uint256) {
    return balances[owner];
}

function totalSupply() public view override returns (uint256) {
    return _totalSupply;
}
}
